---- BANK_CUSTOMERS ---------------------------------------

    CREATE TABLE "BANK_CUSTOMERS" 
   (	
        "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 9999999999
        INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, 
        "GENDER" VARCHAR2(1),  
        "MARITAL_STATUS" VARCHAR2(50), 
        "STREET_ADDRESS" VARCHAR2(255), 
        "CUST_POSTAL_CODE" NUMBER, 
        "CITY" VARCHAR2(50), 
        "STATE_PROVINCE" VARCHAR2(50), 
        "COUNTRY_ID" VARCHAR2(50), 
        "PHONE_NUMBER" VARCHAR2(50), 
        "CUST_INCOME_LEVEL" VARCHAR2(500), 
        "CREDIT_LIMIT" NUMBER, 
        "CUST_VALID" VARCHAR2(1), 
        "CUSTOMER_NAME" VARCHAR2(100),  
        "DOB" DATE, 
        "CUSTOMER_ID" VARCHAR2(20), 
        "ACCOUNT_NUMBER" NUMBER, 
        "ACCOUNT_TYPE" VARCHAR2(20), 
        "REFERENCE_RELATION" VARCHAR2(10), 
        "REFERENCE_NAME" VARCHAR2(100), 
        "COUNTRY" VARCHAR2(50), 
        "CITIZENSHIP" VARCHAR2(100), 
        "OCCUPATION_TYPE" VARCHAR2(50), 
        "INCOME_SOURCE" VARCHAR2(500), 
        "EDUCATIONAL_QUALIFICATION" VARCHAR2(100), 
        "CURRENT_BALANCE" NUMBER, 
        "ACCOUNT_STATUS" VARCHAR2(100), 
        "BANK_NAME" VARCHAR2(100), 
        "UID_PROOF" BLOB, 
        PRIMARY KEY ("ID")
        USING INDEX  ENABLE
   ) ;

---- Debit/Credit Card Table CC_FD -------------------------------------------------

  CREATE TABLE "CC_FD" 
   (	
        "ID" NUMBER GENERATED BY DEFAULT ON NULL AS IDENTITY MINVALUE 1 MAXVALUE 99999999
            INCREMENT BY 1 START WITH 1 CACHE 20 NOORDER  NOCYCLE  NOKEEP  NOSCALE  NOT NULL ENABLE, 
        "CUST_ID" NUMBER, 
        "CC_NO" NUMBER, 
        "STATUS" VARCHAR2(50), 
        "VALIDITY" DATE, 
        "FIRST_NAME" VARCHAR2(50), 
        "LAST_NAME" VARCHAR2(50), 
        "BANK_NAME" VARCHAR2(50), 
        "COMMENTS" VARCHAR2(100), 
        "CARD_TYPE" VARCHAR2(10), 
            PRIMARY KEY ("ID")
        USING INDEX  ENABLE
   ) ;
